import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';

// Components
import Product from './shared/Product';
import Loader from './shared/Loader';

// redux
import { fetchProducts } from '../redux/products/productsAction';

// Style
import styles from "./Store.module.css";

const Store = () => {

    const dispatch = useDispatch();
    const productsState = useSelector(state => state.productsState)
    const [search,setSearch] = useState("")
    useEffect(() => {
       dispatch(fetchProducts())
    }, [])
    const searchedProduct = productsState.products.filter(item => 
        item.title.includes(search) || item.description.includes(search) || item.title.toLowerCase().includes(search.toLowerCase));
    console.log(searchedProduct) 
    return (
        <div className={styles.container} >
            <input type="text" value={search} onChange={(event)=> setSearch(event.target.value)}/>
            {productsState.loading ? 
                <Loader /> :
                productsState.error ?
                    <p>Somethin went wrong</p> :
                    productsState.products.map(product => <Product
                            key={product.id}
                            productData={product}
                        />)
            }
        </div>
    );
};

export default Store;
